    private fun String.parseCase(): D4Case {
        val (y, m, d, h, min, s) = """\[(\d+)-(\d+)-(\d+) (\d+):(\d+)] ((.*|\s)?)""".toRegex().find(this)!!.destructured
        return D4Case (y.toInt(), m.toInt(), d.toInt(), h.toInt(), min.toInt(), s)
    }

    private fun String.toShifts(): List<D4Shift> {
        val cases = split('\n')
                .map{ it.parseCase() }
                .sortedWith(compareBy(D4Case::year, D4Case::month, D4Case::day, D4Case::hour, D4Case::minute))

        val shifts = mutableListOf<D4Shift>()

        var currentShift: D4Shift? = null
        var lastSleepMinute: Int? = null
        cases.forEach { case ->
            when(case.eventType) {
                D4Case.EventType.STARTS_SHIFT -> {
                    currentShift?.let{ shifts.add(it) }
                    currentShift = D4Shift(case.guardNumber)
                }
                D4Case.EventType.FALLS_ASLEEP -> {
                    lastSleepMinute = case.minute
                }
                D4Case.EventType.WAKES_UP -> {
                    lastSleepMinute?.let { startMinute ->
                        (startMinute until case.minute).forEach { currentShift?.minutesAsleep?.add(it) }
                    }
                    lastSleepMinute = null
                }
            }
        }

        currentShift?.let{ shifts.add(it) }

        return shifts
    }

    override fun solveProblem1(input: String): String {
        val shifts = input.toShifts()
        val maxGuard = shifts.groupBy { it.guardNumber }.maxByOrNull { entry -> entry.value.sumOf { it.minutesAsleep.size } }!!
        val minuteCounter = mutableMapOf<Int, Int>()
        maxGuard.value.forEach { shift ->
            shift.minutesAsleep.forEach { minute ->
                minuteCounter[minute] = (minuteCounter[minute]?:0) + 1
            }
        }
        val bestTime = minuteCounter.maxByOrNull { it.value }!!
        return "${maxGuard.key} * ${bestTime.key} = ${maxGuard.key.toInt() * bestTime.key}"
    }

    override fun solveProblem2(input: String): String {
        val allShifts = input.toShifts()
        val mappedShifts = allShifts.groupBy { it.guardNumber }.mapValues { entry ->
            val minuteCounter = mutableMapOf<Int, Int>()
            entry.value.forEach { shift ->
                shift.minutesAsleep.forEach { minute ->
                    minuteCounter[minute] = (minuteCounter[minute]?:0) + 1
                }
            }
            return@mapValues minuteCounter
        }
        val maxGuard = mappedShifts.maxByOrNull { it.value.values.maxOrNull() ?: 0 }!!
        val maxMinute = maxGuard.value.maxByOrNull { it.value }!!.key
        return "${maxGuard.key} * $maxMinute = ${maxGuard.key.toInt() * maxMinute}"
    }

}.solve()

class D4Case(
    val year: Int,
    val month: Int,
    val day: Int,
    val hour: Int,
    val minute: Int,
    event: String
) {
    var guardNumber: String
    val eventType: EventType

    init {
        eventType = when(event) {
            "falls asleep" -> EventType.FALLS_ASLEEP
            "wakes up" -> EventType.WAKES_UP
            else -> EventType.STARTS_SHIFT
        }

        guardNumber = if(eventType == EventType.STARTS_SHIFT) {
            """Guard #(\d+) begins shift""".toRegex().find(event)!!.groups.last()!!.value
        } else {
            ""
        }
    }

    enum class EventType {
        STARTS_SHIFT, FALLS_ASLEEP, WAKES_UP
    }
}

class D4Shift(
        val guardNumber: String
) {
    val minutesAsleep: MutableList<Int> = mutableListOf()
}